<script setup>
  import { gsap } from 'gsap'
  import { onMounted, watch } from 'vue'
  const props = defineProps({
    currentView: String,
  })
  onMounted(() => {
    gsap.set('.nums path:not(#_1)', { opacity: 0 })
    gsap.set('.symbols g:not(#symbol-main)', { opacity: 0 })
  })

  watch(
    () => props.currentView,
    () => {
      gsap.utils.toArray('.nums path').forEach(num => {
        let attr = num.getAttribute('data-symbol')

        if (attr == props.currentView) {
          gsap.to(num, { opacity: 1, x: 0 })
        } else {
          gsap.to(num, { opacity: 0, x: 20 })
        }
      })
      gsap.utils.toArray('.symbols g').forEach(sym => {
        let attr = sym.getAttribute('data-symbol')

        if (attr == props.currentView) {
          gsap.to(sym, { opacity: 1, x: -70, duration: 1 })
        } else {
          gsap.to(sym, { opacity: 0, x: 20, duration: 0.7 })
        }
      })
    }
  )
</script>
<style>
  #counter {
    opacity: 0.6;
  }
</style>
